require.register("BachEditor/js/highlight.js", function (exports, module) {
/**
 * highlight.js
 * @Author integ@segmentfault.com
 * $wrap为空时对所有pre标签执行高亮
 **/
var $ = require('components~jquery@2.1.4');
var hljs = require('btd~highlightjs@7.5.0');

var highLight = function($wrap) {
    //highlight
    // detect highlight.js
    // 自动载入highlight
    var hlNames = {
        actionscript : /^as[1-3]$/i,
        cmake : /^(make|makefile)$/i,
        cs : /^csharp$/i,
        css : /^css[1-3]$/i,
        delphi : /^pascal$/i,
        javascript : /^js$/i,
        markdown : /^md$/i,
        objectivec : /^(oc|objective-c)$/i,
        php  : /^php[1-6]$/i,
        sql : /^mysql$/i,
        xml : /^(html|html5|xhtml)$/i
    };
    var hlLangs = hljs.LANGUAGES;

    function myHighLight($this) {
        var t = $this, children = t.children(), highlighted = false;

        if (children.length > 0 && 'code' === children.get(0).nodeName.toLowerCase()) {
            var className = children.attr('class');

            if (!!className) {
                var classes = className.split(/\s+/);
                for (var i = 0; i < classes.length; i ++) {
                    if (0 === classes[i].indexOf('lang-')) {
                        var lang = classes[i].substring(5).toLowerCase(), finalLang;

                        if (hlLangs[lang]) {
                            finalLang = lang;
                        } else {
                            for (var l in hlNames) {
                                if (lang.match(hlNames[l])) {
                                    finalLang = l;
                                }
                            }
                        }

                        if (!!finalLang) {
                            var result = hljs.highlight(finalLang, children.text(), true);
                            children.html(result.value);
                            highlighted = true;
                            break;
                        }
                    }
                }
            }
        }

        if (!highlighted) {
            var html = t.html();
            t.html(html.replace(/<\/?[a-z]+[^>]*>/ig, ''));
            hljs.highlightBlock($this[0], '', false);
        }

        //添加行号
        t.wrap('<table class="widget-highlight"><tbody><tr><td class="widget-highlight--code"></td></tr></tbody></table>');    //包上table
        var _$wrap = t.parents('.widget-highlight tr');     // tr
        _$wrap.prepend('<td class="widget-highlight--line"></td>'); //插入行号的td
        var _totalLine = t.height() / 15;
        var _$lines = _$wrap.find('.widget-highlight--line');
        // var _$code = _$wrap.find('.widget-highlight--code');
        var _width = t.parents('.widget-highlight').parent().width();
        if(_totalLine < 10) {
            _$lines.css('width', '16px');
            // _$code.css('width', _width - 16 + 'px');
            t.css('width', _width - 32 + 'px');
        } else if(_totalLine < 100) {
            _$lines.css('width', '32px');
            // _$code.css('width', _width - 32 + 'px');
            t.css('width', _width - 40 + 'px');
        } else {
            _$lines.css('width', '48px');
            // _$code.css('width', _width - 48 + 'px');
            t.css('width', _width - 51 + 'px');
        }
        // 黑魔法 dangerous
        if(_totalLine < 17) {
            _totalLine = t.height() / 15;
        } else if(_totalLine < 27) {
            _totalLine = (t.height() - 10) / 15;
        } else if(_totalLine < 55) {
            _totalLine = (t.height() - 20) / 15;
        } else if(_totalLine < 55) {
            _totalLine = (t.height() - 40) / 15;
        } else {
            _totalLine = (t.height() + 15) / 16;
        }
        for(var line = 1; line < _totalLine; line++) {
            var _h = '<p>' + line + '</p>';
            _$lines.append(_h);
        }

        _$lines.attr('unselectable', 'on')      //禁止选中行号
            .css('user-select', 'none')
            .on('selectstart', false);
    }

    // mathJax
    var _hasMath = null;
    if(!$wrap) {
        _hasMath = $('.fmt').text().match(/\$\$/);
        if(_hasMath && _hasMath.length) {
            math();
        }
        $('pre').each(function() {
            if (!this.parentNode || $(this).parents('.CodeMirror-scroll').length) {     //codemirror不参加
                return;
            } else {
                myHighLight($(this));
            }
        });
    } else {
        _hasMath = $wrap.text().match(/\$\$/);
        if(_hasMath && _hasMath.length) {
            math($wrap);
        }
        $wrap.find('pre').each(function() {
            myHighLight($(this));
        });
    }
};
module.exports = highLight;

});

require.register("BachEditor/js/modal.js", function (exports, module) {
'use strict';
var $ = require('components~jquery@2.1.4');
require('components~bootstrap@3.3.5');

function temp(template, data) {
    var str = template || '';
    // Convert the template into string
    $.each(data, function(key, val){
        var _type = typeof val,
            re = new RegExp('{{\\s*' + key + '\\s*}}', 'g');
        if (_type === 'object' && val !== null){
            $.each(val, function(k, v){
                var r = new RegExp('{{\\s*' + key + '.' + k + '\\s*}}', 'g');
                str = str.replace(r, v);
            });
        } else {
            str = str.replace(re, val);
        }
    });
    return str;
};

function sfModal(option) {
    if(typeof option !== 'object') {
        if(option === 'hide') {
            $('.sfmodal').modal('hide');
            return;
        } else if(option === 'toggle') {
            $('.sfmodal').modal('toggle');
            return;
        } else {
            option = {
                content : option,
                hideDone : true,
            };
        }
    }

    var OPT = $.extend({
        hideTitle  : false,
        title      : '警告：前方高能！',
        content    : '玩脱了',
        wrapper      : null,      //编辑器全屏时不能显示modal
        $content   : null,
        hideClose  : false,
        closeText  : '取消',
        // closeFn : function() {},
        hideDone   : false,
        doneText   : '确认',
        doneFn     : function() {
            $('.sfmodal').modal('hide');
        },
        show       : function() {},
        // 不明原因shown不触发
        shown      : function() {},
        hide       : function() {},
        hidden     : function() {},
        loaded     : function() {}
    }, option);

    var dom = '<div class="sfmodal modal" tabindex="-1" role="dialog" aria-labelledby="mySmallModalLabel" aria-hidden="true">\
  <div class="modal-dialog">\
    <div class="modal-content">\
      '+(OPT.hideTitle ? '' : '<div class="modal-header">\
        <button type="button" class="close" data-dismiss="modal"><span aria-hidden="true">&times;</span><span class="sr-only">Close</span></button>\
        <h4 class="modal-title">{{title}}</h4>\
      </div>')+'<div class="modal-body">\
        <p class="sfModal-content">\
          </div>\
          <div class="modal-footer">'
            + (OPT.hideClose ? '' : '<button type="button" class="btn btn-default" data-dismiss="modal">{{closeText}}</button>')
            + (OPT.hideDone ? '' : '<button type="button" class="btn btn-primary done-btn">{{doneText}}</button>')+'</div>\
        </div>\
      </div>\
    </div>';

    // 删掉已经存在的modal
    if ($('.sfmodal').length > 0) {
        $('.sfmodal').remove();
        $('.modal-backdrop').remove();
    }
    // 有$wrap时插到$wrap里面
    if(OPT.wrapper) {
        $(OPT.wrapper).append(temp(dom, OPT));
        $(OPT.wrapper).append('<div class="modal-backdrop in"></div>');
    } else {
        $('body').append(temp(dom, OPT));
    }
    if(OPT.$content) {      // 优先使用$content
        $('.sfmodal .sfModal-content').append(OPT.$content);
    } else {
        $('.sfmodal .sfModal-content').html(OPT.content);
    }
    $('.sfmodal').modal({keyboard: true});
    $('.sfmodal')
        .on('show.bs.modal'  , OPT.show)
        .on('shown.bs.modal' , OPT.shown)
        .on('hide.bs.modal'  , function(e) {
            OPT.hide(e);
            if(OPT.wrapper) {
                $('.modal-backdrop').remove();
            }
        })
        .on('hidden.bs.modal', OPT.hidden)
        .on('loaded.bs.modal', OPT.loaded)
        .modal('show');     // 一定要先绑事件，然后再show
    $('.sfmodal .done-btn').click(function(e) {
        OPT.doneFn(e);
        if(OPT.wrapper) {
            $('.modal-backdrop').remove();
        }
    });
};
module.exports = sfModal;

});

